DROP TABLE IF EXISTS artwork CASCADE;
CREATE TABLE artwork
(
    artworkID bigint NOT NULL,
    artistID bigint NOT NULL REFERENCES artist(artistID),
    name varchar(255)   default NULL,
    year varchar(255)   default NULL,
    technique varchar(255)   default NULL,
    locationID bigint NOT NULL REFERENCES location(locationID),
    artform varchar(255)   default NULL,
